import React from 'react'
import { Link } from 'react-router-dom';
import logo from 'media/logoTSolutions.png';
//import 'Pages/Styles/Login.css';
import useActiveRoute from 'hooks/useActiveRoute';

const Sidebar = () => {

    return (
        <nav className='hidden lg:flex lg:w-72 border border-gray-300 h-full flex-col bg-gray-200 p-4 sidebar'>
            <Link to='/admin'>
                <img className='w-40 ml-9 mt-6 mb-20' src={logo} alt="imagen" />
            </Link>
  

            <div className='my-4'>
                <Ruta icono='fas fa-user' ruta='/admin/Dashboard' nombre='DashBoard' />
                <Ruta icono='fas fa-car' ruta='/admin/listaproductos' nombre='Productos' />
                <Ruta icono='fas fa-cash-register' ruta='/admin/listaventas' nombre='Ventas' />
                <Ruta icono='fas fa-users' ruta='/admin/listausuarios' nombre='Usuarios' />
                <Ruta icono='fas fa-users' ruta='/inicio' nombre='Salir' />
            </div>
            <button>Cerrar Sesi√≥n</button>
        </nav>
    );
};
const Ruta = ({ icono, ruta, nombre }) => {
    const isActive = useActiveRoute(ruta);
    return (
        <Link to={ruta}>
            <button
                className={`p-1 my-2  bg-${isActive ? 'indigo' : 'gray'
                    }-700 hover:bg-indigo-900 flex w-full items-center text-white rounded-md`}
            >
                <i className={`${icono} w-10`} />
                {nombre}
            </button>
        </Link>
    );
};

export default Sidebar;
