import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const SidebarResponsive = () => {
  const [mostrarNavegacion, setMostrarNavegacion] = useState(false);
  return (
    <div
      className='lg:hidden'
      onClick={() => {
        setMostrarNavegacion(!mostrarNavegacion);
      }}
    >
      <i
        className={`mx-2 fas fa-${
          mostrarNavegacion ? 'times' : 'bars'
        } hover:text-indifo-200 cursor-pointer`}
      />
      {mostrarNavegacion && (
        <ul className='bg-gray-200'>
          <ResponsiveRoute ruta='/admin/Dashboard' nombre='DashBoard' />
          <ResponsiveRoute ruta='/admin/listaproductos' nombre='Productos'/>
          <ResponsiveRoute ruta='/admin/listaventas' nombre='Ventas' />
          <ResponsiveRoute ruta='/admin/listausuarios' nombre='Usuarios' />
          <ResponsiveRoute ruta='/inicio' nombre='Salir' />
        </ul>
      )}
    </div>
  );
};

const ResponsiveRoute = ({ ruta, nombre }) => {
  return (
    <Link to={ruta}>
      <li className='text-gray-800 border border-gray-100 p-1'>{nombre}</li>
    </Link>
  );
};

export default SidebarResponsive;
