import axios from 'axios';

const executeRequest = async (options, resCallback, errCallback) => {
  await axios.request(options).then(resCallback).catch(errCallback);
};

export const obtenerVentas = async (setVentas, setEjecutarVentas = () => {}) => {

  const options = { method: 'GET', url: 'http://localhost:5000/ventas/' };

  await axios
    .request(options)
    .then(function (response) {
      setVentas(response.data);
    })
    .catch(function (error) {
      console.error(error);
    });
  setEjecutarVentas(false);
};

export const obtenerUsuarios = async (setVentas, setEjecutarUsuarios = () => {}) => {

  const options = { method: 'GET', url: 'http://localhost:5000/usuarios/' };

  await axios
    .request(options)
    .then(function (response) {
      setVentas(response.data);
  setEjecutarUsuarios(false);
    });
}

export const obtenerProductos = async(resCallback, errCallback) => {
  const options = { method: 'GET', url: 'http://localhost:5000/productos/'};
  await executeRequest(options, resCallback, errCallback);
};


export const crearProducto = async(data, resCallback, errCallback) => {
  const options = {
    method: 'POST',
    url: 'http://localhost:5000/productos/',
    headers: { 'Content-Type': 'application/json' },
    data,
  };
  await executeRequest(options, resCallback, errCallback);
};

export const actualizarProducto = async(id, data, resCallback, errCallback) => {
  const options = {
    method: 'PATCH',
    url: `http://localhost:5000/productos/${id}/`,
    headers: { 'Content-Type': 'application/json' },
    data,
  };
  await executeRequest(options, resCallback, errCallback);
};

export const eliminarProducto = async (id, resCallback, errCallback) => {
  const options = {
    method: 'DELETE',
    url: `http://localhost:5000/productos/${id}/`,
    headers: { 'Content-Type': 'application/json' },
  };
  await executeRequest(options, resCallback, errCallback);
};
