import React, { useEffect, useState, useRef } from "react";
import { ToastContainer, toast } from "react-toastify";
import axios from 'axios';
import { nanoid } from "nanoid";
import { Dialog, Tooltip } from "@material-ui/core";
import { obtenerProductos } from "utils/api";
import "./Styles/Tablas.css";
import "react-toastify/dist/ReactToastify.css";

const Productos = () => {
  // Estados
  const [mostrarTabla, setMostrarTabla] = useState(true);
  const [productos, setProductos] = useState([]); //pata obtener informacion desde el backend
  const [textoBoton, setTextoBoton] = useState("Registrar Producto");
  const [colorBoton, setColorBoton] = useState('indigo');
  const [ejecutarConsulta, setEjecutarConsulta] = useState(true);

  useEffect(() => {
    console.log("consulta", ejecutarConsulta);
    if (ejecutarConsulta) {
      obtenerProductos(setProductos, setEjecutarConsulta);
    }
  }, [ejecutarConsulta]);

  useEffect(() => {
    //obtener lista de productos desde el backend
    if (mostrarTabla) {
      setEjecutarConsulta(true);
    }
  }, [mostrarTabla]);

  useEffect(() => {
    if (mostrarTabla) {
      setTextoBoton('Crear Nuevo Producto');
      setColorBoton('indigo');
    } else {
      setTextoBoton('Mostrar todos los Productos');
      setColorBoton('green');
    }
  }, [mostrarTabla]);
  
  return (
    <div className="flex h-full w-full flex-col items-center justify-start">
      <div
        id="barraNavegador"
        className="bg-indigo-500 mb-16 flex items-center justify-center w-full h-20"
      >
        <nav className="flex text-white">
          <div className="mr-10">
            <ul className="flex">
              <li className="ml-1 mr-4 text-5xl font-semibold">
                Listado Productos
              </li>
            </ul>
          </div>
          <div className="font-sick">
            <ul className="flex mt-1.5">
              <li className="ml-3 mr-4 text-2xl">
                Total: {productos.length}
              </li>
            </ul>
          </div>
        </nav>
      </div>

      <div className="mb-8 flex items-center justify-center w-full h-20">
        <label className="text-base font-semibold mr-5 text-black">
          Buscar:
        </label>
        <svg
          width="24"
          height="24"
          fill="none"
          class="text-gray-400 group-hover:text-gray-500 transition-colors duration-200"
        >
          <path
            d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
            stroke="currentColor"
            stroke-width="2"
            stroke-linecap="round"
            stroke-linejoin="round"
          ></path>
        </svg>
        <input
          type="text"
          id="txtBuscar"
          className="p-2 ml-4 w-96 h-10 rounded-lg border shadow-md"
          placeholder="Buscar por ..."
        ></input>
        <select
          name="select"
          className="font-semibold text-center ml-4 border h-10 rounded-lg shadow-md"
        >
          <option value="value1" selected> Id </option>
          <option value="value2">Descripcion</option>
          <option value="value3">Estado</option>
        </select>
        <Link to="/admin/productos/gestionproductos">      
          <button
            type="button"
            className=" bg-indigo-500 text-white transform hover:scale-110 hover:bg-indigo-600   float-left ml-20 flex items-center p-2 rounded-lg border shadow-md"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              class="mr-2 h-5 w-5"
              viewBox="0 0 20 20"
              fill="currentColor"
            >
              <path
                fill-rule="evenodd"
                d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v2H7a1 1 0 100 2h2v2a1 1 0 102 0v-2h2a1 1 0 100-2h-2V7z"
                clip-rule="evenodd"
              />
            </svg>
            Insertar
          </button>
        </Link>
      </div>
    </div>
  );
};

//componentes para mostar formulario o tabla
const TablaProductos = ({ listaProductos, setEjecutarConsulta }) => {
  const [busqueda, setBusqueda] = useState("");
  const [productosFiltrados, setProductosFiltrados] = useState(listaProductos);

  useEffect(() => {
    setProductosFiltrados(
      listaProductos.filter((elemento) => {
        return JSON.stringify(elemento).toLowerCase().includes(busqueda.toLowerCase());
      })
    );
  }, [busqueda, listaProductos]);

  return (
    <div className="flex flex-col items-center justify-center w-full">
      <div>
        <form className="grid grid-cols-4 m-4">
          <input
            className=" bg-gray-50 border border-gray-200 m-4 p-3 rounded-lg appearance-none relative block px-3 py-1 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
            type="number"
            name="codigo"
            min={0}
            placeholder="Buscar ID Producto"
          />
          <input
            className=" bg-gray-50 border border-gray-200 m-4 p-3 rounded-lg appearance-none relative block px-3 py-1 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
            type="text"
            name="descripcion"
            placeholder="Buscar por descripción"
          />
          <select
            className=" bg-gray-50 border border-gray-200 m-4 rounded-lg appearance-none relative block px-3 py-1 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
            name="buscarEstado"
            defaultValue={0}
          >
            <option disabled value={0}>
              Buscar por estado
            </option>
            <option>Disponible </option>
            <option>No disponible </option>
          </select>
          <button className=" w-px">
            <i className="fas fa-search" />
          </button>
        </form>
      </div>
      <h2 className="text-2xl font-extrabold text-gray-700">Productos</h2>
      <div className='hidden md:flex w-full'>
        <table className="table">
          <thead>
            <tr>
              <th>ID Producto</th>
              <th>Descripción</th>
              <th>Valor Unitario</th>
              <th>Estado</th>
              <th>Acción</th>
            </tr>
          </thead>
          <tbody>
            {productosFiltrados.map((productos) => {
              return (
                <FilaProducto
                  key={nanoid()}
                  productos={productos}
                  setEjecutarConsulta={setEjecutarConsulta}
                />
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};


const FilaProducto = ({ productos, setEjecutarConsulta }) => {
  const [edit, setEdit] = useState(false);
  const [openDialog, setOpenDialog] = useState(false);
  const [infoNuevoProducto, setInfoNuevoProducto] = useState({
    idProducto: productos.idProducto,
    nombreProducto: productos.nombreProducto,
    valorUnitario: productos.valorUnitario,
    estado: productos.estado,
  });

  const actualizarProducto = async () => {
    //enviar la info al backend
    const options = {
      method: "PATCH",
      url: `http://localhost:5000/productos/${productos._id}/`,
      headers: { "Content-Type": "application/json" },
      data: { ...infoNuevoProducto, id: productos._id },
    };

    await axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        toast.success("El producto se ha modificado con éxito");
        setEdit(false);
        setEjecutarConsulta(true);
      })
      .catch(function (error) {
        toast.error("Error al modificar el producto");
        console.error(error);
      });
  };

  const eliminarProducto = async () => {
    const options = {
      method: "DELETE",
      url: `http://localhost:5000/productos/${productos._id}/`,
      headers: { "Content-Type": "application/json" },
      data: { id: productos._id },
    };

    await axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        toast.success("El producto se ha eliminado con éxito");
        setEjecutarConsulta(true);
      })
      .catch(function (error) {
        console.error(error);
        toast.error("Error al eliminar el producto");
      });
    setOpenDialog(false);
  };

  return (
    <tr>
      {edit ? (
        <>
          <td>
            <input
              className="Input"
              type="number"
              value={infoNuevoProducto.idProducto}
              onChange={(e) =>
                setInfoNuevoProducto({
                  ...infoNuevoProducto,
                  idProducto: e.target.value,
                })
              }
            />
          </td>
          <td>
            <input
              className="Input"
              type="text"
              value={infoNuevoProducto.nombreProducto}
              onChange={(e) =>
                setInfoNuevoProducto({
                  ...infoNuevoProducto,
                  nombreProducto: e.target.value,
                })
              }
            />
          </td>
          <td>
            <input
              className="Input"
              type="number"
              value={infoNuevoProducto.valorUnitario}
              onChange={(e) =>
                setInfoNuevoProducto({
                  ...infoNuevoProducto,
                  valorUnitario: e.target.value,
                })
              }
            />
          </td>
          <td>
            <select
              className="Input"
              type="text"
              value={infoNuevoProducto.estado}
              onChange={(e) =>
                setInfoNuevoProducto({
                  ...infoNuevoProducto,
                  estado: e.target.value,
                })
              }
            >
              <option disabled value={0}>
                Seleccione una opción
              </option>
              <option>Disponible </option>
              <option>No disponible </option>
            </select>
          </td>
        </>
      ) : (
        <>

          <td> {productos.idProducto} </td>
          <td> {productos.nombreProducto} </td>
          <td> {productos.valorUnitario} </td>
          <td> {productos.estado} </td>
        </>
      )}
      <td>
        <div className="flex w-full justify-around">
          {edit ? (
            <>
              <Tooltip title="Confirmar Edición" arrow>
                <i
                  onClick={() => actualizarProducto()}
                  className="fas fa-check p-2 hover:bg-blue-600 rounded-full"
                />
              </Tooltip>
              <Tooltip title="Cancelar Edición" arrow>
                <i
                  onClick={() => setEdit(!edit)}
                  className="fas fa-times p-2 hover:bg-blue-600 rounded-full"
                />
              </Tooltip>
            </>
          ) : (
            <>
              <Tooltip title="Editar Producto" arrow>
                <i
                  onClick={() => setEdit(!edit)}
                  className="fas fa-pencil-alt p-2 hover:bg-blue-600 rounded-full"
                />
              </Tooltip>
              <Tooltip title="Elminar Producto" arrow>
                <i
                  onClick={() => setOpenDialog(true)}
                  className="fas fa-trash-alt p-2 hover:bg-blue-600 rounded-full"
                />
              </Tooltip>
            </>
          )}
        </div>
        <Dialog open={openDialog}>
          <div className="p-8 flex flex-col">
            <h1 className="text-gray-900 text-2xl font-bold">
              ¿Está seguro de querer eliminar el producto?
            </h1>
            <div className="flex w-full items-center justify-center my-4">
              <button
                onClick={() => eliminarProducto()}
                className="mx-2 px-4 py-2 bg-blue-500 text-white hover:bg-blue-700 rounded-md shadow-md"
              >
                Sí
              </button>
              <button
                onClick={() => setOpenDialog(false)}
                className="mx-2 px-4 py-2 bg-green-500 text-white hover:bg-green-700 rounded-md shadow-md"
              >
                No
              </button>
            </div>
          </div>
        </Dialog>
      </td>
    </tr>
  );
};


const FormularioProductos = ({
  setMostrarTabla,
  listaProductos,
  setProductos,
  }) => {
  const form = useRef(null);

  const submitForm = async (e) => {
    e.preventDefault();
    const fd = new FormData(form.current);

    const nuevoProducto = {};
    fd.forEach((value, key) => {
      nuevoProducto[key] = value;
    });

    const options = {
      method: "POST",
      url: 'http://localhost:5000/productos/nuevo/',
      headers: { "Content-Type": "application/json" },
      data: {
        idProducto: nuevoProducto.idProducto,
        nombreProducto: nuevoProducto.nombreProducto,
        valorUnitario: nuevoProducto.valorUnitario,
        estado: nuevoProducto.estado,
      },
    };

    await axios
      .request(options)
      .then(function (response) {
        console.log(response.data);
        toast.success("Se ha agregado el producto con éxito");
      })
      .catch(function (error) {
        console.error(error);
        toast.error("Error creando un producto");
      });

    setMostrarTabla(true);

  return (
    <tr>
      {edit ? (
        <>
          <td>
            <input
              className="Input"
              type="number"
              defaultValue={productos.id}
            />
          </td>
          <td>
            <input
              className="Input"
              type="text"
              defaultValue={productos.descripcion}
            />
          </td>
          <td>
            <input
              className="Input"
              type="number"
              defaultValue={productos.valor}
            />
          </td>
          <td>
            <select
              className="Input"
              type="text"
              defaultValue={productos.estado}
            >
              <option disabled value={0}>
                Seleccione una opción
              </option>
              <option>Disponible </option>
              <option>No disponible </option>
            </select>
          </td>
        </>
      ) : (
        <>
          <td> {productos.id} </td>
          <td> {productos.descripcion} </td>
          <td> {productos.valor} </td>
          <td> {productos.estado} </td>
        </>
      )}
      <td>
        <div className="flex w-full justify-around">
          {edit ? (
            <>
              <Tooltip title="Confirmar Edición" arrow>
                <i
                  onClick={() => setEdit(!edit)}
                  className="fas fa-check p-2 text-gray-700 hover:text-green-500 hover:bg-gray-300 rounded-full"
                />
              </Tooltip>
              <Tooltip title="Cancelar Edición" arrow>
                <i
                  onClick={() => setEdit(!edit)}
                  className="fas fa-times p-2 text-gray-700 hover:text-red-500 hover:bg-gray-300 rounded-full"
                />
              </Tooltip>
            </>
          ) : (
            <>
              <Tooltip title="Editar Producto" arrow>
                <i
                  onClick={() => setEdit(!edit)}
                    className="fas fa-pencil-alt p-2 text-gray-700 hover:text-green-500 hover:bg-gray-300 rounded-full"
                />
              </Tooltip>
              <Tooltip title="Eliminar Producto" arrow>
                <i
                  onClick={() => setOpenDialog(true)}
                    className="fas fa-trash-alt p-2 text-gray-700 hover:text-red-500 hover:bg-gray-300  rounded-full"
                />
              </Tooltip>
            </>
          )}
        </div>
        <Dialog open={openDialog}>
          <div className="p-8 flex flex-col">
            <h1 className="text-gray-800 text-xl font-bold">
              ¿Está seguro de querer eliminar el producto?
            </h1>
            <div className="flex w-full items-center justify-center my-4">
              <button className="mx-2 px-4 py-2 bg-green-400 text-white hover:bg-green-600 rounded-md shadow-md">
                Sí
              </button>
              <button
                onClick={() => setOpenDialog(false)}
                className="mx-2 px-4 py-2 bg-red-400 text-white hover:bg-red-600 rounded-md shadow-md"
              >
                No
              </button>
            </div>
          </div>
        </Dialog>
      </td>
    </tr>
  );
};
};

  export default ListProductos;
